import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Register({ onRegister }) {
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // Para exibir mensagens de erro
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Reseta mensagem de erro ao tentar registrar

    try {
      const response = await axios.post('http://localhost:8082/www/4SM/pj2/pj2/backend/public/register.php', {
        phone,
        password
      });

      if (response.data.success) {
        onRegister(response.data.username); // Autentica o usuário com o nome retornado
        navigate('/home'); // Redireciona para a Home
      } else {
        setErrorMessage('Falha ao registrar. Verifique seus dados.'); // Exibe erro se falhar
      }
    } catch (error) {
      console.error('Error registering:', error);
      setErrorMessage('Erro no servidor. Tente novamente mais tarde.'); // Exibe erro de servidor
    }
  };

  return (
    <div>
      <h2>Registro</h2>
      <form onSubmit={handleRegister}>
        <div>
          <label>Telefone:</label>
          <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>
        <div>
          <label>Senha:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="submit">Registrar</button>
      </form>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      <div>
        <p>Já tem uma conta? <a href="/login">Faça login</a></p>
      </div>
    </div>
  );
}

export default Register;
