// src/pages/Register.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Register({ onRegister }) {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [acceptTerms, setAcceptTerms] = useState(false); // Novo estado para a caixa de seleção
  const navigate = useNavigate(); 

  const handleRegister = async (e) => {
    e.preventDefault();
    
    if (!acceptTerms) {
      alert('Você deve aceitar os termos e condições.');
      return;
    }

    try {
      await axios.post('http://localhost/backend/public/register.php', {
        name,
        phone,
        password
      });

      onRegister(name); // Chama a função de autenticação com o nome do usuário
      navigate('/login');
    } catch (error) {
      console.error('Error registering:', error);
    }
  };

  return (
    <div>
      <h2>Cadastro de Usuário</h2>
      <form onSubmit={handleRegister}>
        <div>
          <label>Nome:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Telefone:</label>
          <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>
        <div>
          <label>Senha:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <input type="checkbox" checked={acceptTerms} onChange={(e) => setAcceptTerms(e.target.checked)} />
          <label> Aceito os termos e condições</label>
        </div>
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default Register;
