import React, { useState } from 'react';
import axios from 'axios';
import '../styles/CreatePost.css';

const axiosInstance = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
    withCredentials: true, // Isso deve ser verdadeiro para enviar cookies/sessões
});

const CreatePost = ({ onClose, onCreatePost }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        if (image) {
            formData.append('image', image);
        }

        try {
            const response = await axiosInstance.post('/create_post.php', formData);
            if (response.data && response.data.id) {
                onCreatePost(response.data);
            } else {
                console.error('Erro ao criar post:', response.data);
            }
        } catch (error) {
            console.error('Erro ao enviar post:', error);
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="create-post-modal">
            <h2>Criar Post</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Descrição"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                />
                <input
                    type="file"
                    onChange={(e) => setImage(e.target.files[0])}
                />
                <button type="submit" disabled={isSubmitting}>
                    {isSubmitting ? 'Criando...' : 'Criar Post'}
                </button>
                <button type="button" onClick={onClose}>Cancelar</button>
            </form>
        </div>
    );
};

export default CreatePost;
