import React, { useState } from 'react';

function ForgotPassword() {
  const [telefone, setTelefone] = useState('');
  const [mensagem, setMensagem] = useState('');

  const handleForgotPassword = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/forgot_password.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ phone: telefone }),
      });

      const data = await response.json();

      if (data.success) {
        // Exibir a mensagem em um alerta
        window.alert(`Nome de Usuário: ${data.username}\nTelefone: ${data.phone}\nMensagem: ${data.message}`);
      } else {
        setMensagem(data.message);
      }
    } catch (error) {
      setMensagem('Erro ao conectar com o servidor.');
    }
  };

  return (
    <div>
      <h2>Esqueci a Senha</h2>
      <form onSubmit={handleForgotPassword}>
        <div>
          <label>Telefone:</label>
          <input
            type="text"
            value={telefone}
            onChange={(e) => setTelefone(e.target.value)}
          />
        </div>
        <button type="submit">Recuperar Senha</button>
      </form>
      {mensagem && <p>{mensagem}</p>}
    </div>
  );
}

export default ForgotPassword;
