import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from '../components/Post';

function Blog() {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchPosts() {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/posts.php`);
                console.log('Dados retornados:', response.data); // Verifique a estrutura dos dados
                if (Array.isArray(response.data)) {
                    setPosts(response.data);
                } else {
                    setError('A resposta não é um array.');
                }
            } catch (error) {
                setError('Erro ao buscar posts.');
            } finally {
                setLoading(false);
            }
        }
    
        fetchPosts();
    }, []);

    if (loading) {
        return <p>Carregando posts...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (!posts || posts.length === 0) {
        return <p>Sem posts disponíveis</p>;
    }

    return (
        <div>
            <h2>Blog</h2>
            {posts.map((post) => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    {/* Verifique se o campo images existe e tem imagens */}
                    {post.images && post.images.length > 0 ? (
                        post.images.map((image, idx) => (
                            <img
                                key={idx}
                                src={image}
                                alt={`Post ${post.id} - Imagem ${idx + 1}`}
                                style={{ width: '100%', height: 'auto', marginBottom: '10px' }}
                            />
                        ))
                    ) : (
                        <p>Sem imagens para este post</p>
                    )}
                    {/* Renderiza o componente Post (se necessário, ajuste as props) */}
                    <Post post={post} />
                </div>
            ))}
        </div>
    );
}

export default Blog;
