import { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Header from './components/Header';
import Home from './pages/Home';
import Blog from './pages/Blog';
import Plan from './pages/Plan';
import Profile from './pages/Profile';
import Register from './pages/Register'; // Importando a página de cadastro
import Login from './pages/Login'; // Importando a página de login
import ForgotPassword from './pages/ForgotPassword'; // Importando a página de esqueci a senha
import Terms from './pages/Terms'; // Importando a página de termos de uso
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Estado de autenticação

  useEffect(() => {
    // Fazendo a requisição para o back-end
    axios.get('http://localhost/backend/public/index.php')
      .then((res) => {
        setPosts(res.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleAuthentication = () => {
    setIsAuthenticated(true);
  };

  return (
    <Router>
      <Header />
      <Routes>
        {/* Exibir a página de cadastro como rota inicial */}
        <Route path="/" element={<Register onRegister={handleAuthentication} />} />
        {/* Rota para a página de login */}
        <Route path="/login" element={<Login onLogin={handleAuthentication} />} />
        {/* Rota para a página de esqueci a senha */}
        <Route path="/forgot-password" element={<ForgotPassword />} />
        {/* Rota para a página de termos de uso */}
        <Route path="/terms" element={<Terms />} />
        {/* Rota para a página home, acessível apenas se o usuário estiver autenticado */}
        <Route path="/home" element={isAuthenticated ? <Home /> : <Login onLogin={handleAuthentication} />} />
        <Route path="/blog" element={<Blog />} />
        <Route path="/plan" element={<Plan />} />
        <Route path="/profile" element={<Profile />} />
      </Routes>
    </Router>
  );
}

export default App;
