import { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Home from './pages/Home';
import Blog from './pages/Blog';
import Plan from './pages/Plan';
import Profile from './pages/Profile';
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // Fazendo a requisição para o back-end
    axios.get('http://localhost/backend/public/index.php')
      .then((res) => {
        setPosts(res.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={
          <div className="App">
            <header className="App-header">
              <h1>Posts de Arquitetura, Engenharia Civil e Design de Interiores</h1>
            </header>

            <div className="posts-container">
              {
                posts.length > 0 ? (
                  posts.map((post, index) => (
                    <div className="post" key={index}>
                      <img src={post.imagem} alt={`Post ${index}`} className="post-image" />
                      <p className="post-content">{post.conteudo}</p>
                    </div>
                  ))
                ) : (
                  <p>Carregando posts...</p>
                )
              }
            </div>
          </div>
        } />
        <Route path="/home" element={<Home />} />
        <Route path="/blog" element={<Blog />} />
        <Route path="/plan" element={<Plan />} />
        <Route path="/profile" element={<Profile />} />
      </Routes>
    </Router>
  );
}

export default App;
