import React, { useState } from 'react';
import axios from 'axios';
import '../styles/CreatePost.css'; // Importar o CSS

const CreatePost = ({ onClose, onCreatePost }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Cria um novo FormData para enviar os dados do post
        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        if (image) {
            formData.append('image', image);
        }

        try {
            // Envia a requisição POST para criar um novo post
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/create_post.php`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data', // Define o tipo de conteúdo como multipart/form-data
                },
            });

            // Chama a função onCreatePost passando o novo post recebido da resposta
            onCreatePost(response.data);
            onClose(); // Fecha o modal após a criação do post
        } catch (error) {
            console.error('Erro ao criar o post:', error);
        }
    };

    return (
        <div className="create-post-container">
            <div className="create-post-modal">
                <h2>Criar Post</h2>
                <form onSubmit={handleSubmit}>
                    <input 
                        type="text" 
                        placeholder="Título" 
                        value={title} 
                        onChange={(e) => setTitle(e.target.value)} 
                        required 
                    />
                    <textarea 
                        placeholder="Descrição" 
                        value={description} 
                        onChange={(e) => setDescription(e.target.value)} 
                        required 
                    ></textarea>
                    <input 
                        type="file" 
                        onChange={(e) => setImage(e.target.files[0])} 
                    />
                    <div className="button-container">
                        <button type="submit" className="submit">Enviar</button>
                        <button type="button" className="cancel" onClick={onClose}>Cancelar</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreatePost;
