import { useEffect, useState } from 'react';
import axios from 'axios';
import { Route, Routes, useNavigate, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Home from './pages/Home';
import Blog from './pages/Blog';
import Profile from './pages/Profile';
import Register from './pages/Register';
import Login from './pages/Login';
import ForgotPassword from './pages/ForgotPassword';
import Terms from './pages/Terms';
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Checar se o usuário está autenticado no localStorage ao carregar o app
    const storedUser = localStorage.getItem('username');
    if (storedUser) {
      setIsAuthenticated(true);
      setUsername(storedUser);
      // Redireciona apenas se estiver na rota raiz
      if (window.location.pathname === '/' || window.location.pathname === '/register') {
        navigate('/home');
      }
    } else {
      if (window.location.pathname === '/') {
        navigate('/register');
      }
    }
  }, [navigate]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/index.php`);
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPosts();
  }, []);

  const handleAuthentication = (user) => {
    setIsAuthenticated(true);
    setUsername(user);

    // Armazenar o usuário autenticado no localStorage
    localStorage.setItem('username', user);

    navigate('/home');
  };

  return (
    <>
      {isAuthenticated && <Header username={username} />}
      <Routes>
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login onLogin={handleAuthentication} />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/terms" element={<Terms />} />
        <Route 
          path="/home" 
          element={isAuthenticated ? <Home username={username} isAuthenticated={isAuthenticated} /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/blog" 
          element={isAuthenticated ? <Blog /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/profile" 
          element={isAuthenticated ? <Profile /> : <Navigate to="/login" />} 
        />
        <Route path="*" element={<Navigate to="/register" />} />
      </Routes>
    </>
  );
}

export default App;
