function App() {
  const [posts, setPosts] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [tags, setTags] = useState(['arquitetura', 'engenharia', 'design']); // Exemplo de tags
  const [popularPosts, setPopularPosts] = useState(['Post 1', 'Post 2', 'Post 3']); // Exemplo de posts populares

  const navigate = useNavigate();
  const location = useLocation();

  const handleAuthentication = (user) => {
    setIsAuthenticated(true);
    setUsername(user);
    navigate('/home');
  };

  useEffect(() => {
    if (window.location.pathname === '/') {
      navigate('/register');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/index.php`);
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPosts();
  }, []);

  return (
    <>
      {isAuthenticated && (
        <Header 
          username={username} 
          setIsAuthenticated={setIsAuthenticated} 
          tags={tags} 
          popularPosts={popularPosts} 
        />
      )}
      <Routes>
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login onLogin={handleAuthentication} />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/terms" element={<Terms />} />
        <Route 
          path="/home" 
          element={isAuthenticated ? <Home username={username} posts={posts} /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/blog" 
          element={isAuthenticated ? <Blog posts={posts} /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/profile" 
          element={isAuthenticated ? <Profile /> : <Navigate to="/login" />} 
        />
        <Route path="*" element={<Navigate to="/register" />} />
      </Routes>

      {location.pathname === '/home' && <Footer />}
    </>
  );
}

export default App;
