// src/components/Header.js
import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import { FaHome, FaUser, FaSignOutAlt } from 'react-icons/fa'; // Importando o ícone de logout
import '../styles/Header.css';

const Header = ({ username }) => {
  const history = useHistory();

  const handleLogout = () => {
    // Remover o usuário do localStorage ou qualquer outra lógica de logout
    localStorage.removeItem('user'); // Supondo que você armazene os dados do usuário assim
    history.push('/login'); // Redirecionar para a página de login após o logout
  };

  return (
    <header className="header">
      <nav className="navbar">
        <div className="navbar-logo">
          <Link to="/home">
            <FaHome size={30} />
          </Link>
        </div>
        <ul className="navbar-links">
          <li>
            <Link to="/home">Home</Link>
          </li>
          <li>
            <Link to="/blog">Blog</Link>
          </li>
        </ul>
        <div className="navbar-user">
          <Link to="/profile">
            <FaUser size={30} />
          </Link>
          {/* Exibir o nome do usuário ao lado do ícone de perfil */}
          {username && <span className="username">Olá, {username}</span>}
          {/* Adicionando o botão de logout */}
          {username && (
            <button className="logout-button" onClick={handleLogout}>
              <FaSignOutAlt size={20} /> Sair
            </button>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
