// src/pages/Blog.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Blog() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await axios.get('http://localhost/backend/public/posts.php');
        setPosts(response.data); // Certifique-se de que a resposta está correta
      } catch (error) {
        console.error('Erro ao buscar posts:', error);
      }
    }

    fetchPosts();
  }, []);

  if (!posts || posts.length === 0) {
    return <p>Sem posts disponíveis</p>;
  }

  return (
    <div>
      <h2>Blog</h2>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Blog;
