import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHome, FaUser, FaSignOutAl, FaBars } from 'react-icons/fa';
import '../styles/Header.css';

const Header = ({ username, setIsAuthenticated }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      // Chamada para o endpoint de logout
      await fetch(`${process.env.REACT_APP_API_URL}/logout.php`, { method: 'POST' });
  
      // Atualiza o estado de autenticação
      setIsAuthenticated(false);
  
      // Após o logout, redirecionar para a página de login
      navigate('/login');
    } catch (error) {
      console.error('Erro ao realizar logout:', error);
    }
  };  
    const handleSearch = (e) => {
      e.preventDefault();
      // Redirecionar para a página de resultados de busca (ou qualquer funcionalidade desejada)
      console.log(`Buscando por: ${searchQuery}`);
      // Implementar a lógica de navegação para resultados de busca
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen); // Abre/fecha a Sidebar
  };

  return (
    <header className="header">
      <nav className="navbar">
        <div className="navbar-logo">
          <Link to="/home">
            <FaHome size={30} />
          </Link>
        </div>
        <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Pesquisar..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Buscar</button>
        </form>
        <div className="navbar-user">
          <Link to="/profile">
            <FaUser size={30} />
          </Link>

          {/* Adicionando o botão de logout */}
          {username && (
            <button className="logout-button" onClick={handleLogout}>
              <FaSignOutAlt size={20} /> Sair
            </button>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
