import { useEffect, useState } from 'react';
import axios from 'axios';
import { Route, Routes, useNavigate } from 'react-router-dom'; // Removi o BrowserRouter daqui
import Header from './components/Header';
import Home from './pages/Home';
import Blog from './pages/Blog';
import Plan from './pages/Plan';
import Profile from './pages/Profile';
import Register from './pages/Register';
import Login from './pages/Login';
import ForgotPassword from './pages/ForgotPassword';
import Terms from './pages/Terms';
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Estado de autenticação
  const [username, setUsername] = useState(''); // Estado do nome de usuário

  const navigate = useNavigate(); // Para redirecionar entre páginas

  useEffect(() => {
    axios.get('http://localhost/backend/public/index.php')
      .then((res) => {
        setPosts(res.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleAuthentication = (user) => {
    setIsAuthenticated(true);
    setUsername(user); // Seta o nome de usuário quando autentica
    navigate('/home'); // Redireciona para a Home após autenticação
  };

  return (
    <>
      {/* Só exibe o Header se o usuário estiver autenticado */}
      {isAuthenticated && <Header username={username} />}

      <Routes>
        <Route path="/" element={<Register onRegister={handleAuthentication} />} />
        <Route path="/login" element={<Login onLogin={handleAuthentication} />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/terms" element={<Terms />} />
        <Route path="/home" element={isAuthenticated ? <Home /> : <Login onLogin={handleAuthentication} />} />
        <Route path="/blog" element={<Blog />} />
        <Route path="/plan" element={<Plan />} />
        <Route path="/profile" element={isAuthenticated ? <Profile /> : <Login onLogin={handleAuthentication} />} />
      </Routes>
    </>
  );
}

export default App;
