import React from 'react';

const Post = ({ post }) => {
    return (
        <div>
            <h1>{post.title}</h1>
            <p>{post.description}</p>
            {post.images && post.images.length > 0 && (
                <div className="post-images">
                    {post.images.map((img, index) => (
                        <img 
                            key={index} 
                            src={img} 
                            alt={`Imagem ${index + 1} do post`}
                            style={{ width: '100%', marginBottom: '10px' }} 
                        />
                    ))}
                </div>
            )}
        </div>
    );
};

export default Post;
