import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHome, FaUser, FaSignOutAlt } from 'react-icons/fa';
import '../styles/Header.css';

const Header = ({ username, setIsAuthenticated }) => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      // Chamada para o endpoint de logout
      await fetch('http://localhost/backend/public/logout.php', { method: 'POST' });

      // Atualiza o estado de autenticação
      setIsAuthenticated(false);

      // Após o logout, redirecionar para a página de login
      navigate('/login');
    } catch (error) {
      console.error('Erro ao realizar logout:', error);
    }
  };

  return (
    <header className="header">
      <nav className="navbar">
        <div className="navbar-logo">
          <Link to="/home">
            <FaHome size={30} />
          </Link>
        </div>
        <ul className="navbar-links">
          <li>
            <Link to="/home">Home</Link>
          </li>
          <li>
            <Link to="/blog">Blog</Link>
          </li>
        </ul>
        <div className="navbar-user">
          <Link to="/profile">
            <FaUser size={30} />
          </Link>
          {/* Exibir o nome do usuário ao lado do ícone de perfil */}
          {username && <span className="username">Olá, {username}</span>}
          {/* Adicionando o botão de logout */}
          {username && (
            <button className="logout-button" onClick={handleLogout}>
              <FaSignOutAlt size={20} /> Sair
            </button>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
