// src/pages/Blog.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Blog() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/posts.php`);
        setPosts(response.data);
      } catch (error) {
        // Captura de erro detalhado
        if (error.response) {
          setError(`Erro: ${error.response.status} - ${error.response.data}`);
        } else if (error.request) {
          setError('Sem resposta do servidor. Tente novamente mais tarde.');
        } else {
          setError(`Erro: ${error.message}`);
        }
        console.error('Erro ao buscar posts:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchPosts();
  }, []);

  if (loading) {
    return <p>Carregando posts...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (!posts || posts.length === 0) {
    return <p>Sem posts dispon√≠veis</p>;
  }

  return (
    <div>
      <h2>Blog</h2>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.description}</p>
            {post.images && post.images.length > 0 && (
              <div>
                {post.images.map((img, index) => (
                  <img key={index} src={img} alt={post.title} style={{ width: '100px' }} />
                ))}
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Blog;
