import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHome, FaUser, FaSignOutAlt, FaBars } from 'react-icons/fa'; // FaBars para o ícone de menu
import Sidebar from './Sidebar'; // Importando a Sidebar
import '../styles/Header.css';

const Header = ({ username, setIsAuthenticated, tags, popularPosts }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(false); // Estado para controlar a Sidebar
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await fetch(`${process.env.REACT_APP_API_URL}/logout.php`, { method: 'POST' });
      setIsAuthenticated(false);
      navigate('/login');
    } catch (error) {
      console.error('Erro ao realizar logout:', error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    console.log(`Buscando por: ${searchQuery}`);
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen); // Abre/fecha a Sidebar
  };

  return (
    <header className="header">
      <nav className="navbar">
        <div className="navbar-logo">
          <FaBars size={30} onClick={toggleSidebar} /> {/* Ícone de menu */}
          <Link to="/home">
            <FaHome size={30} />
          </Link>
        </div>
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Pesquisar..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit">Buscar</button>
        </form>
        <div className="navbar-user">
          <Link to="/profile">
            <FaUser size={30} />
          </Link>
          {username && (
            <button className="logout-button" onClick={handleLogout}>
              <FaSignOutAlt size={20} /> Sair
            </button>
          )}
        </div>
      </nav>

      {sidebarOpen && <Sidebar tags={tags} popularPosts={popularPosts} />} {/* Exibe a Sidebar */}
    </header>
  );
};

export default Header;
