import { useEffect, useState } from 'react';
import axios from 'axios';
import { Route, Routes, useNavigate, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Home from './pages/Home';
import Blog from './pages/Blog';
import Profile from './pages/Profile';
import Register from './pages/Register';
import Login from './pages/Login';
import ForgotPassword from './pages/ForgotPassword';
import Terms from './pages/Terms';
import './styles/App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    // Checar se o usuário está autenticado no localStorage ao carregar o app
    const storedUser = localStorage.getItem('username');
    if (storedUser) {
      setIsAuthenticated(true);
      setUsername(storedUser);
    }
  }, []);

  const handleAuthentication = (user) => {
    setIsAuthenticated(true);
    setUsername(user);

    // Armazenar o usuário autenticado no localStorage
    localStorage.setItem('username', user);

    navigate('/home');
  };

  return (
    <>
      {isAuthenticated && <Header username={username} />}
      <Routes>
        <Route path="/" element={<Register onRegister={handleAuthentication} />} />
        <Route path="/login" element={<Login onLogin={handleAuthentication} />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/terms" element={<Terms />} />
        <Route path="/home" element={isAuthenticated ? <Home /> : <Navigate to="/login" />} />
        <Route path="/blog" element={isAuthenticated ? <Blog /> : <Navigate to="/login" />} />
        <Route path="/profile" element={isAuthenticated ? <Profile /> : <Navigate to="/login" />} />
      </Routes>
    </>
  );
}

export default App;
