import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHome, FaUser, FaSignOutAlt, FaBars } from 'react-icons/fa'; // Adicione FaBars para o ícone de menu
import Sidebar from './Sidebar'; // Importe a Sidebar
import '../styles/Header.css';

const Header = ({ username, setIsAuthenticated }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Estado para controlar a Sidebar
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await fetch(`${process.env.REACT_APP_API_URL}/logout.php`, { method: 'POST' });
      setIsAuthenticated(false);
      navigate('/login');
    } catch (error) {
      console.error('Erro ao realizar logout:', error);
    }
  };  

  const handleSearch = (e) => {
    e.preventDefault();
    console.log(`Buscando por: ${searchQuery}`);
  };

  // Função para abrir/fechar a Sidebar
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <>
      <header className="header">
        <nav className="navbar">
          <div className="navbar-logo">
            <FaBars size={30} onClick={toggleSidebar} /> {/* Botão para abrir a Sidebar */}
            <Link to="/home">
              <FaHome size={30} />
            </Link>
          </div>
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Pesquisar..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Buscar</button>
          </form>
          <div className="navbar-user">
            <Link to="/profile">
              <FaUser size={30} />
            </Link>
            {username && (
              <button className="logout-button" onClick={handleLogout}>
                <FaSignOutAlt size={20} /> Sair
              </button>
            )}
          </div>
        </nav>
      </header>

      {/* Sidebar será exibida com base no estado */}
      {isSidebarOpen && <Sidebar tags={['React', 'PHP', 'DevOps']} popularPosts={['Post 1', 'Post 2', 'Post 3']} />} 
    </>
  );
};

export default Header;
